class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#new-comment-form-${e}`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.destroyComment($(this))})),$(" .comment-toggle-like-button",this.postContainer).each((function(){t.destroyComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(o){o.preventDefault();$.ajax({type:"post",url:$(this).prop("action"),data:$(this).serialize(),success:o=>{let n=t.newCommentDom(o.data.comment);$(`#post-comments-${e}`).prepend(n),t.destroyComment($(" .delete-comment-button",n)),new ToggleLike($(" .toggle-like-button",n)),new Noty({theme:"relax",text:"Commented",type:"success",layout:"topCenter",timeout:1500}).show()},error:e=>{console.log(e.responseText)}})}))}newCommentDom=e=>{let t=new Date(e.updatedAt).toLocaleString("default",{month:"short",day:"numeric",year:"numeric",hour:"numeric",minute:"numeric"});return $(`<li id="comment-${e._id}" class="comment-li">\n                    <div class="comment">\n                            <a class="delete-comment-button" href="/comments/destroy/${e._id}"><i class="bi bi-x-circle"></i></a>\n                            ${e.content}\n                        <p>\n                            <small>\n                                ${t}\n                            </small>\n                            <small style="margin-right: .2rem;">\n                                ${e.user.name}\n                            </small>\n                            <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment" style="color: black ; text-decoration: none ;"">\n                                0 <i class="bi bi-hand-thumbs-up-fill"></i>\n                            </a>\n                        </p>\n                    </div>\n                </li>`)};destroyComment=e=>{$(e).click((t=>{t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:e=>{$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"error",layout:"topCenter",timeout:1500}).show()},error:e=>{console.log(e.responseText)}})}))}}