import{toggleLike}from"./toggle_like.js";export const createComment=e=>{let t=$(`#new-comment-form-${e}`);t.submit((o=>{o.preventDefault(),$.ajax({type:"post",url:t.prop("action"),data:t.serialize(),success:t=>{let o=newCommentDom(t.data.comment);$(`#post-comments-${e}`).prepend(o),destroyComment($(" .delete-comment-button",o)),toggleLike($(" .comment-toggle-like-button",o)),new Noty({theme:"relax",text:"Commented",type:"success",layout:"topCenter",timeout:1500}).show()},error:e=>{console.log(e.responseText)}}),$(`#new-comment-form-${e} input:first-child`).val("")}))};const newCommentDom=e=>{let t=new Date(e.updatedAt).toLocaleString("default",{month:"short",day:"numeric",year:"numeric",hour:"numeric",minute:"numeric"});return $(`<li id="comment-${e._id}" class="comment-li">\n                    <div class="comment">\n                            <a class="delete-comment-button" href="/comments/destroy/${e._id}"><i class="bi bi-x-circle"></i></a>\n                            ${e.content}\n                        <p>\n                            <small>\n                                ${t}\n                            </small>\n                            <small style="margin-right: .2rem;">\n                                ${e.user.name}\n                            </small>\n                            <a class="comment-toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment" style="color: black ; text-decoration: none ;"">\n                                0 <i class="bi bi-hand-thumbs-up-fill"></i>\n                            </a>\n                        </p>\n                    </div>\n                </li>`)};export const destroyComment=e=>{$(e).click((t=>{t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:e=>{$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"error",layout:"topCenter",timeout:1500}).show()},error:e=>{console.log(e.responseText)}})}))};const addingDeleteToComments=()=>{let e=$(".comment-li");Array.from(e).forEach((e=>{let t=$(" .delete-comment-button",e);destroyComment(t),toggleLike($(" .comment-toggle-like-button",e))}))};addingDeleteToComments();