const toggleFriendship=()=>{let e=$("#btn a");e.click((t=>{t.preventDefault(),$.ajax({type:"get",url:e.prop("href"),success:t=>{t.data.added?(new Noty({theme:"relax",text:"Added to Friends",type:"success",layout:"topCenter",timeout:1500}).show(),e.text("Remove Friend")):(new Noty({theme:"relax",text:"Friend Removed",type:"error",layout:"topCenter",timeout:1500}).show(),e.text("Add to friend"))},error:e=>{console.log(e.responseText)}})}))};toggleFriendship();