class ToggleLike{constructor(e){this.toggler=e,this.toggleLike()}toggleLike(){$(this.toggler).click((function(e){e.preventDefault();let t=this;$.ajax({type:"post",url:$(t).attr("href"),success:e=>{let l,s=parseInt($(t).attr("data-likes"));console.log(s),1==e.data.deleted?(s-=1,l="black"):(s+=1,l="blue"),$(t).attr("data-likes",s),$(t).html(`${s} <i class="bi bi-hand-thumbs-up-fill"></i>`),$(t).css("color",`${l}`)},error:e=>{console.log("error in toggle likes",e.responseText)}})}))}}