export const toggleLike=e=>{$(e).click((t=>{t.preventDefault(),$.ajax({type:"post",url:$(e).attr("href"),success:t=>{let l,s=parseInt($(e).attr("data-likes"));console.log(s),1==t.data.deleted?(s-=1,l="black"):(s+=1,l="blue"),$(e).attr("data-likes",s),$(e).html(`${s} <i class="bi bi-hand-thumbs-up-fill"></i>`),$(e).css("color",`${l}`)},error:e=>{console.log("error in toggle likes",e.responseText)}})}))};